INSERT INTO `haberler` (`id`, `baslik`, `kategori`, `icerik`, `gorsel_url`, `tarih`) VALUES
(9, 'ADANA SICAKTAN YANIYOR', 'Gündem', 'ADANA 80 DERECE', 'https://cdnuploads.aa.com.tr/uploads/Contents/2022/07/02/thumbs_b_c_caa7afcee6d6988e607d0c65c524b867.jpg?v=154319', '2025-05-13 20:50:06'),
(10, 'İşte Trabzonspor\'un Kupadaki ilk 11\'i !', 'Spor', 'Trabzonspor\'un ilk 11\'i belli oldu\r\n\r\nUğurcan, Pedro, Savic, Batagov, Mustafa, Okay, Ozan, Sikan, Zubkov, Nwakaeme, Banza', 'https://flashhabertv.com.tr/wp-content/uploads/2024/11/325f8bf390b447a59fd2c4aa6c512142.jpg', '2025-05-13 22:04:20'),
(12, 'UAEA: İranda nükleer sızıntı var', 'Son Dakika', 'Uluslararası Atom Enerjisi Kurumu, İranda nükleer sızıntı olduğunu bildirdi.', 'https://trthaberstatic.cdn.wp.trt.com.tr/resimler/1730000/iran-nukleer-aa-1731298_2.jpg', '2025-06-13 19:30:00'),
(13, 'Sözleşmeli öğretmen alımı sözlü sınav giriş belgeleri erişime açıldı', 'Son Dakika', 'Milli Eğitim Bakanlığınca, 15 bin sözleşmeli öğretmen ataması için yapılacak sözlü sınavın aday giriş belgeleri erişime açıldı.', 'https://trthaberstatic.cdn.wp.trt.com.tr/resimler/1758000/ogretmen-1758763_2.jpg', '2025-06-13 19:19:00'),
(14, 'Türkiye ve Yunanistan arasındaki Ortak Eylem Planı toplantısı Ankara\'da düzenlendi', 'Son Dakika', 'Türkiye ve Yunanistan arasındaki Pozitif Gündem diyaloğu kapsamında Ortak Eylem Planının 8. Toplantısı Ankara\'da düzenlendi.', 'https://trthaberstatic.cdn.wp.trt.com.tr/resimler/2370000/turkiye-yunanistan-ortak-eylem-plani-toplantisi-disisleri-2370828_2.jpg', '2025-06-13 19:01:00'),
(15, 'İran itidal çağrılarını reddederek, İsrail\'e saldırıların hukuka uygun, sert ve kararlı olacağını duyurdu', 'Son Dakika', 'İran Dışişleri Bakanı Abbas Erakçi, İsrail\'e karşı saldırıların ardından gelen itidal çağrılarını reddederek, \"İran\'ın İsrail\'in saldırganlığına cevabı, BM Şartı\'na uygun olarak sert ve kararlı olacak.\" dedi.', 'https://trthaberstatic.cdn.wp.trt.com.tr/resimler/1476000/iran-1477871_2.jpg', '2025-06-13 19:04:00'),
(16, 'Bakan Işıkhan: Yaklaşık 550 bin engelli vatandaşımızın istihdamına aracılık ettik', 'Son Dakika', 'Çalışma ve Sosyal Güvenlik Bakanı Vedat Işıkhan, \"2002\'den bu yana yaklaşık 550 bin engelli vatandaşımızın istihdama katılmasına aracılık ettik. 2024\'te bu sayı 45 bini aşmış, 2025 yılı ilk çeyreği itibarıyla yaklaşık 17 bine ulaşmıştır\" dedi.', 'https://trthaberstatic.cdn.wp.trt.com.tr/resimler/2370000/vedat-isikhan-aa-2370827_2.jpg', '2025-06-13 18:38:00'),
(17, 'Kırıkkale\'de mesleki eğitim öğrencilerine fabrikada eğitim imkanı', 'Son Dakika', 'Makine ve Kimya Endüstrisi ile Milli Eğitim Bakanlığı Mesleki ve Teknik Eğitim Genel Müdürlüğü arasında imzalanan \"Sektör İçi Mesleki Eğitim Merkezi İş Birliği Protokolü\" ile Kırıkkale\'deki MKE Mühimmat Fabrikasında mesleki eğitim öğrencilerine doğrudan fabrika üretim hattının içinde eğitim imkanı sağlanacak.', 'https://trthaberstatic.cdn.wp.trt.com.tr/resimler/1918000/meslek-lisesi-mesleki-egitim-mebgovtr-1919901_2.jpg', '2025-06-13 18:09:00'),
(18, 'Macron: İsrail\'in savunmasına katkı sağlayabiliriz', 'Son Dakika', 'Fransa Cumhurbaşkanı Emmanuel Macron, İran\'ın, Tel Aviv hükümetinin saldırılarına misilleme olarak bu ülkeye karşılık vermesi durumunda İsrail\'in savunmasına katkıda bulunabileceklerini ifade etti.', 'https://trthaberstatic.cdn.wp.trt.com.tr/resimler/2194000/macron-2195384_2.jpg', '2025-06-13 18:27:00'),
(19, 'Turistik Karaelmas Ekspresi ilk seferine çıktı', 'Son Dakika', 'Ankara ile Zonguldak arasında işletilecek Turistik Karaelmas Ekspresi ilk seferini gerçekleştirmek üzere yola çıktı.', 'https://trthaberstatic.cdn.wp.trt.com.tr/resimler/2370000/karaelmas-2370389_2.jpg', '2025-06-13 17:54:00'),
(20, 'BM: Gazze\'nin orta ve güney bölgelerine hizmet veren internet bağlantısı kalmadı', 'Son Dakika', 'Birleşmiş Milletler BM Genel Sekreter Sözcü Yardımcısı Stephanie Tremblay, İsrail\'in saldırılarının ve ablukasının devam ettiği Gazze\'nin orta ve güney bölgelerine internet sağlayan herhangi bir bağlantının kalmadığını belirtti.', 'https://trthaberstatic.cdn.wp.trt.com.tr/resimler/2360000/gazze-aa-2361582_2.jpg', '2025-06-13 17:59:00'),
(21, 'Tel Aviv\'den dumanlar yükseliyor', 'Son Dakika', 'İran, İsrail\'e karşı balistik füze saldırısı başlattı. Füzelerden biri Tel Aviv\'deki Savunma Bakanlığı\'nın çok yakınına isabet etti.', 'https://trthaberstatic.cdn.wp.trt.com.tr/resimler/2370000/sshjhdfhfhd-2370810_2.jpg', '2025-06-13 17:31:00'),
(22, 'İran lideri Hamaney, İsrail\'in bu suçtan sağ kurtulamayacağını söyledi', 'Son Dakika', 'İran lideri Ali Hamaney, İsrail\'in ülkesine yönelik saldırıları nedeniyle işlediği suçtan sağ kurtulamayacağını belirtti.', 'https://trthaberstatic.cdn.wp.trt.com.tr/resimler/1502000/ayetullah-ali-hamaneyin-1503684_2.jpg', '2025-06-13 17:28:00'),
(23, 'İsrailin iletişimi kestiği Gazzede gazeteciler, gerçeği dünyaya duyurmak için hayatlarını riske atıyor', 'Son Dakika', 'Gazze Şeridinde İsrailin altyapıyı hedef alan saldırıları nedeniyle internet ve iletişim hizmetlerinin tamamen kesilmesinin ardından, Filistinli gazeteciler üçüncü gününde de dünyaya gerçeği ulaştırmak için hayatlarını riske atıyor.', 'https://trthaberstatic.cdn.wp.trt.com.tr/resimler/2370000/2370809_2.jpg', '2025-06-13 17:02:00'),
(24, 'FSM Köprüsü\'nde zincirleme trafik kazası: 1 kişi öldü, 3 kişi yaralandı', 'Son Dakika', 'Fatih Sultan Mehmet Köprüsü\'nde meydana gelen zincirleme trafik kazasında 1 kişi hayatını kaybetti, 3 kişi yaralandı.', 'https://trthaberstatic.cdn.wp.trt.com.tr/resimler/2370000/fsm-kaza-aa-2370796_2.jpg', '2025-06-13 16:04:00'),
(25, 'ABD Başkanı Trump, İsrailİran gerilimini Durum Odası\'nda ulusal güvenlik ekibiyle değerlendiriyor', 'Son Dakika', 'ABD Başkanı Donald Trump, İsrail\'in İran\'a yönelik saldırılarının ardından Orta Doğu\'da artan gerilime ilişkin son durumu Beyaz Saray\'daki Durum Odası\'nda ulusal güvenlik ekibiyle değerlendiriyor.', 'https://trthaberstatic.cdn.wp.trt.com.tr/resimler/2360000/trump-reu-2360277_2.jpg', '2025-06-13 16:02:00'),
(26, 'Netanyahu, düzenledikleri saldırılara İran\'ın karşılık vereceğini söyledi', 'Son Dakika', 'İsrail Başbakanı Binyamin Netanyahu, İran\'a ilk etapta nisan sonu için saldırı planladıklarını ancak ertelediklerini belirterek İsraillilere \"İran karşılık verecek, füzeler gelecek. Coşkuya kapılmayın, talimatları dinleyin\" dedi.', 'https://trthaberstatic.cdn.wp.trt.com.tr/resimler/2236000/2236908_2.jpg', '2025-06-13 15:56:00'),
(27, 'Tahran\'da yeniden patlama sesleri: Nükleer tesis hedef alındı', 'Son Dakika', 'İranın yarı resmi Fars Haber Ajansı, İsrail saldırılarının sürdüğü İranda, Fordo nükleer tesisinin hedef alındığını duyurdu.', 'https://trthaberstatic.cdn.wp.trt.com.tr/resimler/2370000/tahran-hava-2370797_2.jpg', '2025-06-13 14:52:00'),
(28, 'Bakan Fidan, Irak ve Ürdünlü mevkidaşlarıyla İsrail\'in İran saldırısını görüştü', 'Son Dakika', 'Dışişleri Bakanı Hakan Fidan, Irak Dışişleri Bakanı Fuad Hüseyin ve Ürdün Dışişleri Bakanı Ayman Safadi ile telefon görüşmeleri gerçekleştirdi. Görüşmelerde, İsrailin İrana yönelik saldırısıyla ilgili gelişmeler ele alındı.', 'https://trthaberstatic.cdn.wp.trt.com.tr/resimler/2314000/hakan-fidan-aa-2315296_2.jpg', '2025-06-13 14:33:00'),
(29, 'Ukrayna, Rusya\'dan 1200 Ukraynalı asker ve sivilin cenazesini teslim aldı', 'Son Dakika', 'Türkiye\'nin ev sahipliğinde Haziran\'ın başında İstanbul\'daki müzakerelerde varılan anlaşmalar kapsamında, 1200 asker ve sivilin cenazesinin Ukrayna\'ya iade edildiği bildirildi.', 'https://trthaberstatic.cdn.wp.trt.com.tr/resimler/2066000/ukrayna-rusya-asker-drone-dron-reu-2067025_2.jpg', '2025-06-13 14:17:00'),
(30, 'Katil İsrail\'in İran\'a yönelik saldırılarına 8 ay öncesinden hazırlandığı iddiası', 'Son Dakika', 'İsrail\'in İran\'ın nükleer tesisler, füze üsleri, bilim insanları ve üst düzey askeri yetkililerini hedef alan saldırılarının 8 aylık gizli hazırlığın ardından yapıldığı iddia edildi.', 'https://trthaberstatic.cdn.wp.trt.com.tr/resimler/2370000/israil-saldiri-aa-2370436_2.jpg', '2025-06-13 13:43:00'),
(31, 'ABD\'nin İran\'ın İsrail\'e misilleme ihtimaline karşı askeri varlıklarını bölgeye yönlendirdiği öne sürüldü', 'Son Dakika', 'ABD\'nin, İran\'ın İsrail\'e misilleme ihtimaline karşın, savaş gemileri dahil askeri varlıklarını Orta Doğu\'ya kaydırdığı iddia edildi.', 'https://trthaberstatic.cdn.wp.trt.com.tr/resimler/1550000/israil-abd-reu-1551270_2.jpg', '2025-06-13 13:28:00'),
(32, 'Bakan Göktaş: 64 bin 238 genci kamuda istihdam edeceğiz', 'Son Dakika', 'Aile ve Sosyal Hizmetler Bakanı Mahinur Özdemir Göktaş, devlet korumasından yararlanmış gençlerin kamu kurumlarına yerleştirilmesi için düzenlenen kura töreninde, \"Bugün gerçekleşen yerleştirmeyle beraber 64 bin 238 genci kamuda istihdam edeceğiz\" dedi.', 'https://trthaberstatic.cdn.wp.trt.com.tr/resimler/2202000/mahinur-ozdemir-goktas-aa-2202068_2.jpg', '2025-06-13 13:13:00'),
(33, 'Hindistan\'da düşen uçağı dakikalar ile kaçıran yolcu kazadan kurtuldu', 'Son Dakika', 'Hindistan\'ın Gucerat eyaletinin Ahmedabad kentinde kalkıştan kısa bir süre sonra düşen Air India uçağını 10 dakika farkla kaçıran bir yolcu, kazadan \"mucizevi\" şekilde kurtuldu.', 'https://trthaberstatic.cdn.wp.trt.com.tr/resimler/2370000/hindistan-ucak-kazasi-2370213_2.jpg', '2025-06-13 13:12:00'),
(34, 'İsrail, yeni saldırı dalgasında Tebriz, Kirmanşah ve Hemedan\'ı hedef aldı', 'Son Dakika', 'İsrail, İran\'a başlattığı son birkaç saatteki saldırılarında Tebriz, Hemedan ve Kirmanşah\'ı vurdu.', 'https://trthaberstatic.cdn.wp.trt.com.tr/resimler/2370000/tebriz-2370650_2.jpg', '2025-06-13 13:11:00'),
(35, 'Fidan, Güler, Kalın ve Gürak İsrail saldırılarını görüştü', 'Son Dakika', 'Dışişleri Bakanı Hakan Fidan, Milli Savunma Bakanı Yaşar Güler, Genelkurmay Başkanı Metin Gürak ve MİT Başkanı İbrahim Kalın, İsrail saldırısıyla başlayan süreç ve muhtemel yansımalar için bir araya geldi.', 'https://trthaberstatic.cdn.wp.trt.com.tr/resimler/2370000/gorusme-2370777_2.jpg', '2025-06-13 13:04:00'),
(36, 'BM Güvenlik Konseyi, İran\'ın talebiyle bugün toplanacak', 'Son Dakika', 'İran\'ın talebi üzerine Birleşmiş Milletler BM Güvenlik Konseyi bugün, İsrail\'in İran\'a yönelik saldırılarını ele almak üzere toplanıyor. İran, BM\'ye gönderdiği mektupta İsrail\'in \"yasa dışı ve korkakça\" saldırılarına kararlı yanıt vereceğini bildirdi.', 'https://trthaberstatic.cdn.wp.trt.com.tr/resimler/2214000/bmgk-aa-2215219_2.jpg', '2025-06-13 12:54:00'),
(37, 'YÖK, geleceğin mesleklerine yönelik yeni programlar açacak', 'Son Dakika', 'Yükseköğretim Kurulu Başkanlığı YÖK, dijital dönüşüm, yapay zeka, bilişim teknolojileri, tarım ve sağlıkta dijitalleşme ile yeşil dönüşüm alanlarında yeni ön lisans ve lisans programlarını öğrencilerin tercihine sunacak.', 'https://trthaberstatic.cdn.wp.trt.com.tr/resimler/1722000/yazilim-bilgisayar-getty-1723018_2.jpg', '2025-06-13 12:50:00'),
(38, 'Hüseyin Goncagül son yolculuğuna uğurlandı', 'Son Dakika', 'Çocuklara yönelik tiyatro çalışmalarıyla tanınan oyuncu, programcı, sunucu ve yönetmen Hüseyin Goncagül, İstanbul\'da son yolculuğuna uğurlandı.', 'https://trthaberstatic.cdn.wp.trt.com.tr/resimler/2370000/cenaze-toreni-aa-2370776_2.jpg', '2025-06-13 12:40:00'),
(39, 'Uzmanından kene sayısında olağanüstü bir artış yok açıklaması', 'Son Dakika', 'Erciyes Üniversitesi ERÜ Veteriner Fakültesi Parazitoloji Ana Bilim Dalı Başkanı Abdullah İnci, kene sayısında önceki yıllara göre artış yaşandığı yönündeki paylaşımlara ilişkin, \"Ne Sivas\'ta ne Kayseri\'de sıkıntı var. Türkiye\'nin herhangi bir yerinde olağanüstü bir durum yok. Tedbirlerimizi gevşetmeden uygulayacağız. Halkımız sakin olsun\" dedi.', 'https://trthaberstatic.cdn.wp.trt.com.tr/resimler/1566000/kene-aa-1566000_2.jpg', '2025-06-13 12:34:00'),
(40, 'Devlet Bahçeli: İsrail, saldırılarını coğrafyanın her yerine taşımaktadır', 'Son Dakika', 'MHP Genel Başkanı Bahçeli, İsrail\'in İran\'a yönelik saldırısına ilişkin, \"İsrail önüne gelene saldırı düzenlemekte, sabotaj ve suikast kurgusuyla örtülü operasyonlarını coğrafyanın her yerine taşımaktadır\" değerlendirmesinde bulundu.', 'https://trthaberstatic.cdn.wp.trt.com.tr/resimler/2246000/devlet-bahceliaa-2247371_2.jpg', '2025-06-13 12:23:00'),
(41, 'Gazze\'deki ablukanın kırılması için Mısır\'a gelen 40 aktivist alıkonuldu', 'Son Dakika', 'Küresel Gazze Yürüyüşü Hareketi, Gazze\'deki ablukanın kırılması için çeşitli ülkelerden Mısır\'a ulaşan 40 aktivistin alıkonulduğunu açıkladı.', 'https://trthaberstatic.cdn.wp.trt.com.tr/resimler/2368000/misir-gazze-gosteriaa-2368842_2.jpg', '2025-06-13 12:23:00'),
(42, '3 Madleen aktivisti İran saldırıları nedeniyle İsrail\'de kaldı', 'Son Dakika', 'İran\'a saldırılar nedeniyle iptal edilen uçuşların ardından \"Madleen\" gemisinden alıkonulan 3 insan hakları aktivistinin İsrail\'de kaldığı duyuruldu.', 'https://trthaberstatic.cdn.wp.trt.com.tr/resimler/2368000/madleen-2368453_2.jpg', '2025-06-13 12:19:00'),
(43, 'BM: Aktivistlerin Gazze\'ye yürümesi, küresel bir hayal kırıklığının ifadesidir', 'Son Dakika', 'Birleşmiş Milletler İnsani İşler Koordinasyon Ofisi UNOCHA Sözcüsü Jens Laerke, aktivistlerin ablukayı kırmak için Gazze\'ye yürüyüş eylemleri düzenlemesine ilişkin, \"Bunun çözümsüzlükten kaynaklanan küresel bir hayal kırıklığının ifadesi olduğunu düşünüyorum.\" dedi.', 'https://trthaberstatic.cdn.wp.trt.com.tr/resimler/2368000/libya-konvoy-gazze-konvoy-2369826_2.jpg', '2025-06-13 12:17:00'),
(44, 'İran: Natanz Nükleer Tesisi içinde nükleer kirlilik tespit edildi', 'Son Dakika', 'İran Atom Enerjisi Kurumu Sözcüsü Behruz Kemalvendi, İsrail\'in İsfahan kentindeki Natanz Nükleer Tesisi\'ne saldırısı sonrasında tesis içerisinde nükleer kirlilik meydana geldiğini söyledi.', 'https://trthaberstatic.cdn.wp.trt.com.tr/resimler/1556000/natanz-nuclear-facility-iran-reu-1557666_2.jpg', '2025-06-13 12:07:00'),
(45, 'İran Cumhurbaşkanı: İran\'ın cevabı düşmanı pişman edecek', 'Son Dakika', 'İran Cumhurbaşkanı Mesud Pezeşkiyan, \"İsraile güçlü bir cevap vererek düşmanı yaptıklarına pişman edeceklerini\" söyledi.', 'https://trthaberstatic.cdn.wp.trt.com.tr/resimler/2280000/pezeskiyan-a-2281787_2.jpg', '2025-06-13 12:05:00'),
(46, 'Hizbullah: İsrailin İrana saldırısı tüm kırmızı çizgileri aştı', 'Son Dakika', 'Lübnandaki Hizbullah Hareketi, İsrailin İrana yönelik geniş çaplı saldırısını \"kırmızı çizgilerin tehlikeli bir şekilde aşılması\" olarak niteledi.', 'https://trthaberstatic.cdn.wp.trt.com.tr/resimler/2354000/iran-patlama-2354789_2.jpg', '2025-06-13 12:01:00'),
(47, '4 ülkeye uçak seferleri 16 Haziran\'a kadar iptal', 'Son Dakika', 'Ulaştırma ve Altyapı Bakanı Abdulkadir Uraloğlu, \"Başta Türk Hava Yolları olmak üzere, ilgili havayolu işletmelerimiz, 16 Haziran 2025 Pazartesi gününe kadar İran, Irak, Suriye ve Ürdüne yönelik seferlerini iptal etmiştir\" duyurusunda bulundu.', 'https://trthaberstatic.cdn.wp.trt.com.tr/resimler/2302000/ucak-depophotos-2302655_2.jpg', '2025-06-13 11:27:00'),
(48, 'İranın başkenti Tahranda halktan intikam çağrısı', 'Son Dakika', 'İranın başkenti Tahranda cuma namazı sonrası yürüyüş düzenleyen İranlılar, İsrailin saldırılarını lanetlerken yetkililerden aynı sertlikte cevap vermesini istedi.', 'https://trthaberstatic.cdn.wp.trt.com.tr/resimler/2364000/iran-aa-2365452_2.jpg', '2025-06-13 11:29:00'),
(49, 'Katil İsrail\'in Tahran saldırılarında 78 kişi hayatını kaybetti, 329 kişi yaralandı', 'Son Dakika', 'İran medyası, İsrail\'in Tahran\'daki yerleşim yerlerine düzenlediği saldırılar sonucunda 78 kişinin hayatını kaybettiğini, 329 kişinin ise yaralandığını bildirdi.', 'https://trthaberstatic.cdn.wp.trt.com.tr/resimler/2370000/iran-2370443_2.jpg', '2025-06-13 11:19:00'),
(50, 'Cumhurbaşkanı Erdoğan\'dan, tiyatro sanatçısı Hüseyin Goncagül için taziye mesajı', 'Son Dakika', 'Cumhurbaşkanı Recep Tayyip Erdoğan, vefat eden tiyatro oyuncusu Hüseyin Goncagül için taziye mesajı yayımladı.', 'https://trthaberstatic.cdn.wp.trt.com.tr/resimler/2370000/huseyin-gocagul-2370393_2.jpg', '2025-06-13 11:10:00'),
(51, 'Marmaray\'da babayı darbeden şüpheliye 4 yıla kadar hapis istemi', 'Son Dakika', 'Marmaray\'da çocuklarının yanında darbedilen babaya saldırarak burnunu kırdığı iddiasıyla hakkında soruşturma açılan şüphelinin 4 yıl 6 aya kadar hapsi talep edildi.', 'https://trthaberstatic.cdn.wp.trt.com.tr/resimler/2364000/marmaray-babaya-darp-iha-2365975_2.jpg', '2025-06-13 11:03:00'),
(52, 'Trump: İsrail, İran\'a daha fazla saldırı düzenleyecek', 'Son Dakika', 'ABD Başkanı Donald Trump, İsrail\'in İran\'a düzenlediği saldırıları \"mükemmel\" diye nitelendirerek \"Ve dahası gelecek, çok daha fazlası.\" dedi.', 'https://trthaberstatic.cdn.wp.trt.com.tr/resimler/2356000/trump-reu-2357768_2.jpg', '2025-06-13 10:55:00'),
(53, 'İranda internete kısıtlama getirildi', 'Son Dakika', 'İsrailin İrana yönelik saldırılarının ardından İletişim Bakanlığı ülke genelinde internetin kısıtlandığını, durum normale dönünce kısıtlamanın sonlandırılacağını duyurdu.', 'https://trthaberstatic.cdn.wp.trt.com.tr/resimler/1612000/iran-elektrik-kesintisi-aa-1612443_2.jpg', '2025-06-13 10:53:00'),
(54, 'Kur Korumalı Mevduat\'tan çıkışlar artarak sürüyor', 'Son Dakika', 'Kur Korumalı Türk Lirası Mevduat ve Katılma Hesapları KKM, geçen hafta 4 milyar 710 milyon lira azalarak 571 milyar 516 milyon liraya düştü. KKM büyüklüğü, toplam mevduatın yüzde 2,6\'sı oldu.', 'https://trthaberstatic.cdn.wp.trt.com.tr/resimler/1478000/para-1479243_2.jpg', '2025-06-13 10:51:00'),
(55, 'ITA Airways, Tel Aviv seferlerini askıya alma kararını uzattı', 'Son Dakika', 'İtalya\'nın bayrak taşıyıcı hava yolu şirketi ITA Airways, Orta Doğu\'daki mevcut durum nedeniyle İsrail\'in başkenti Tel Aviv için uçuşların askıya alınması kararını 31 Temmuz\'a kadar uzatacağını bildirdi.', 'https://trthaberstatic.cdn.wp.trt.com.tr/resimler/1968000/ita-airways-reu-1968634_2.jpg', '2025-06-13 10:44:00'),
(56, 'Katil İsrail Ordu Sözcüsü Defrin: Uzun süreli bir savaşa hazırlıklı olmalıyız', 'Son Dakika', 'İsrail Ordu Sözcüsü Effie Defrin, İran\'a yönelik saldırıları sürdürdüklerini belirterek \"Uzun sürecek bir operasyona hazırlanmamız gerekiyor.\" dedi.', 'https://trthaberstatic.cdn.wp.trt.com.tr/resimler/2356000/israil-reu-2356123_2.jpg', '2025-06-13 10:34:00'),
(57, 'Binlerce hacı, cuma namazını Mescidi Nebevi\'de kıldı', 'Son Dakika', 'Mukaddes topraklarda hacı olan Müslümanlar, Medine\'de cuma namazını Hazreti Muhammed\'in yapımında çalıştığı ve kabrinin bulunduğu Mescidi Nebevi\'de kıldı.', 'https://trthaberstatic.cdn.wp.trt.com.tr/resimler/2370000/haci-namaz-aa-2370695_2.jpg', '2025-06-13 10:39:00'),
(58, 'Yaz aylarında sıcaklık rekoru beklenmiyor', 'Son Dakika', 'Boğaziçi Üniversitesi İklim Değişikliği ve Politikaları Uygulama ve Araştırma Merkezi Müdürü Prof. Dr. Levent Kurnaz, en sıcak ikinci bahar mevsiminin yaşanmasının ardından 2025 yaz döneminde büyük olasılıkla sıcaklık rekoru beklenmediğini, bu senenin 2024 ve 2023\'ten sonra en sıcak üçüncü yıl olacağını söyledi.', 'https://trthaberstatic.cdn.wp.trt.com.tr/resimler/2082000/istanbul-sicak-2083533_2.jpg', '2025-06-13 10:38:00'),
(59, 'Temizliğin simgesi Mavi Bayraklar tatilciye güven veriyor', 'Son Dakika', 'Türkiye Çevre Eğitim Vakfı (TÜRÇEV) Yönetim Kurulu Başkanı Abdulkadir Ateş, Türkiye\'nin 1993\'te 1 olan Mavi Bayrak sayısının 577\'ye çıkmasının önemini belirterek, \"Mavi Bayrak tatilciye güven veriyor.\" dedi.', 'https://trthaberstatic.cdn.wp.trt.com.tr/resimler/1868000/mavi-bayrak-antalya-aa-1868486.jpg', '2025-06-13 10:25:00'),
(60, 'Cumhurbaşkanı Erdoğan: İsrail haydutluğuna artık bir dur denmelidir', 'Son Dakika', 'Cumhurbaşkanı Recep Tayyip Erdoğan, \"Uluslararası toplum, küresel ve bölgesel istikrarı hedef alan İsrail haydutluğuna artık bir dur demelidir. Netanyahu ve katliam şebekesinin tüm bölgemizi ateşe atan saldırılarının mutlaka önüne geçilmelidir.\" dedi.', 'https://trthaberstatic.cdn.wp.trt.com.tr/resimler/2366000/erdogan-aa-2366683.jpg', '2025-06-13 10:14:00'),
(61, 'Hamsi, Türkiye denizlerinin değişmeyen lideri', 'Son Dakika', 'Türkiye denizlerinde, son 10 yılda en çok avlanan türde 1 milyon 689 bin 98 tonla hamsi liderliğini sürdürürken, bu balığı çaça, palamut, sardalya, istavrit ve lüfer takip etti.', 'https://trthaberstatic.cdn.wp.trt.com.tr/resimler/1980000/hamsi-edirne-aa-1980730.jpg', '2025-06-13 10:05:00'),
